import React, { useState } from "react";
import { trainModels, predictRisk, predictGrade } from "./api";
import Dashboard from "./components/Dashboard";
import ModelSelector from "./components/ModelSelector";
import ResultsTable from "./components/ResultsTable";
import UploadDataset from "./components/UploadDataset";
import Charts from "./components/Charts";

function App() {
  const [input, setInput] = useState({
    studytime: "",
    absences: "",
    G1: "",
    G2: "",
    failures: "",
  });
  const [riskResult, setRiskResult] = useState(null);
  const [gradeResult, setGradeResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleTrain = async () => {
    setLoading(true);
    setError(null);
    try {
      await trainModels();
      alert("Models trained successfully!");
    } catch (err) {
      setError("Training failed.");
    }
    setLoading(false);
  };

  const handlePredictRisk = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await predictRisk(input);
      setRiskResult(res.data.risk_level);
    } catch (err) {
      setError("Prediction failed.");
    }
    setLoading(false);
  };

  const handlePredictGrade = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await predictGrade(input);
      setGradeResult(res.data.predicted_grade);
    } catch (err) {
      setError("Prediction failed.");
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>Student Performance Decision Support System</h1>
      <button onClick={handleTrain} disabled={loading}>
        Train Models
      </button>
      <div>
        <h2>Enter Student Data</h2>
        <input
          name="studytime"
          placeholder="Study Time"
          value={input.studytime}
          onChange={handleChange}
        />
        <input
          name="absences"
          placeholder="Absences"
          value={input.absences}
          onChange={handleChange}
        />
        <input
          name="G1"
          placeholder="G1"
          value={input.G1}
          onChange={handleChange}
        />
        <input
          name="G2"
          placeholder="G2"
          value={input.G2}
          onChange={handleChange}
        />
        <input
          name="failures"
          placeholder="Failures"
          value={input.failures}
          onChange={handleChange}
        />
        <button onClick={handlePredictRisk} disabled={loading}>
          Predict Risk
        </button>
        <button onClick={handlePredictGrade} disabled={loading}>
          Predict Grade
        </button>
      </div>
      {riskResult && (
        <div>
          <strong>Risk Level:</strong> {riskResult}
        </div>
      )}
      {gradeResult && (
        <div>
          <strong>Predicted Grade:</strong> {gradeResult}
        </div>
      )}
      {error && <div style={{ color: "red" }}>{error}</div>}
      <Dashboard />
      <ModelSelector />
      <ResultsTable />
      <UploadDataset />
      <Charts />
    </div>
  );
}

export default App;
